"use client";

import React from "react";
import { cn } from "@/lib/utils";
import { cva, type VariantProps } from "class-variance-authority";

const tooltipVariants = cva(
  "absolute z-50 select-none px-3 py-2 text-sm transition-all duration-300",
  {
    variants: {
      style: {
        watercolor:
          "rounded-[40%_60%_70%_30%/40%_40%_60%_50%] border border-white/20 bg-gradient-to-br from-[#7FB3D5]/90 to-[#5499C7]/80 text-white shadow-lg shadow-[#7FB3D5]/20 backdrop-blur-md",
        oil: "rounded-[60%_40%_50%_50%/60%_50%_40%_40%] border border-white/10 bg-gradient-to-br from-[#2C3E50]/95 to-[#34495E]/85 text-white shadow-lg shadow-[#2C3E50]/30 backdrop-blur-md",
        pencil:
          "rounded-[30%_70%_60%_40%/30%_40%_70%_60%] border border-white/10 bg-gradient-to-br from-zinc-800/90 to-zinc-900/80 text-white shadow-lg shadow-zinc-900/30 backdrop-blur-md",
      },
      position: {
        top: "mb-3 -translate-x-1/2 -translate-y-full",
        bottom: "mt-3 -translate-x-1/2 translate-y-full",
        left: "mr-3 -translate-x-full -translate-y-1/2",
        right: "ml-3 -translate-y-1/2 translate-x-full",
      },
    },
    defaultVariants: {
      style: "watercolor",
      position: "top",
    },
  }
);

export type ArtisticTooltipProps = React.HTMLAttributes<HTMLDivElement> &
  VariantProps<typeof tooltipVariants> & {
    content: string;
    show?: boolean;
    children: React.ReactNode;
  };

export default function ArtisticTooltip({
  className,
  style,
  position,
  content,
  show = false,
  children,
  ...props
}: ArtisticTooltipProps) {
  return (
    <div className="relative inline-block">
      {children}
      {show && (
        <div
          className={cn(
            tooltipVariants({ style, position }),
            "duration-200 animate-in fade-in-0 zoom-in-[98%]",
            "after:absolute after:h-2 after:w-2 after:rotate-45",
            position === "top" && "after:-bottom-1 after:left-1/2 after:-translate-x-1/2 after:rounded-sm after:bg-inherit",
            position === "bottom" && "after:-top-1 after:left-1/2 after:-translate-x-1/2 after:rounded-sm after:bg-inherit",
            position === "left" && "after:-right-1 after:top-1/2 after:-translate-y-1/2 after:rounded-sm after:bg-inherit",
            position === "right" && "after:-left-1 after:top-1/2 after:-translate-y-1/2 after:rounded-sm after:bg-inherit",
            className
          )}
          {...props}
        >
          <span className="relative block">{content}</span>
        </div>
      )}
    </div>
  );
}
