"use client";

import React from "react";
import { cn } from "@/lib/utils";
import { Command } from "lucide-react";

interface ShortcutItemProps {
  keys: string[];
  description: string;
  category: string;
}

const ShortcutItem = ({ keys, description }: ShortcutItemProps) => (
  <div className="group flex items-center justify-between rounded-lg p-2 transition-colors hover:bg-accent/50">
    <span className="text-sm text-muted-foreground group-hover:text-foreground">
      {description}
    </span>
    <div className="flex gap-1">
      {keys.map((key, index) => (
        <React.Fragment key={index}>
          <kbd className="pointer-events-none inline-flex h-7 select-none items-center gap-1 rounded border bg-muted px-2.5 font-mono text-[12px] font-medium text-muted-foreground hover:bg-accent hover:text-accent-foreground">
            {key}
          </kbd>
          {index < keys.length - 1 && (
            <span className="text-muted-foreground">+</span>
          )}
        </React.Fragment>
      ))}
    </div>
  </div>
);

interface StudioShortcutsGuideProps {
  className?: string;
}

const shortcuts = {
  general: [
    { keys: ["Ctrl", "S"], description: "Save current work" },
    { keys: ["Ctrl", "Z"], description: "Undo last action" },
    { keys: ["Ctrl", "Y"], description: "Redo last action" },
  ],
  tools: [
    { keys: ["B"], description: "Brush tool" },
    { keys: ["E"], description: "Eraser tool" },
    { keys: ["M"], description: "Move tool" },
    { keys: ["L"], description: "Lasso tool" },
  ],
  canvas: [
    { keys: ["Space"], description: "Pan canvas" },
    { keys: ["Ctrl", "+"], description: "Zoom in" },
    { keys: ["Ctrl", "-"], description: "Zoom out" },
    { keys: ["Ctrl", "0"], description: "Reset zoom" },
  ],
  layers: [
    { keys: ["Ctrl", "J"], description: "Duplicate layer" },
    { keys: ["Ctrl", "G"], description: "Group layers" },
    { keys: ["["], description: "Lower layer opacity" },
    { keys: ["]"], description: "Increase layer opacity" },
  ],
};

export default function StudioShortcutsGuide({
  className,
}: StudioShortcutsGuideProps) {
  return (
    <div
      className={cn(
        "rounded-lg border bg-card text-card-foreground shadow-sm",
        className,
      )}
    >
      <div className="flex items-center border-b bg-muted/50 px-4 py-2">
        <Command className="mr-2 h-4 w-4" />
        <h3 className="text-sm font-semibold">Keyboard Shortcuts</h3>
      </div>
      <div className="p-4 pt-2">
        {Object.entries(shortcuts).map(([category, items]) => (
          <div key={category} className="mb-4 last:mb-0">
            <h4 className="mb-2 text-sm font-medium capitalize text-foreground/70">
              {category}
            </h4>
            <div className="space-y-1">
              {items.map((shortcut, index) => (
                <ShortcutItem
                  key={index}
                  keys={shortcut.keys}
                  description={shortcut.description}
                  category={category}
                />
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
